,p_plsql_code=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'  /*-------------------------------------',
'   * GuidedTour Functions',
'   * Version: 1.0 (03.08.2015)',
'   * Author:  Daniel Hochleitner',
'   *-------------------------------------',
'  */',
'FUNCTION render_shepherdnewinstance(p_dynamic_action IN apex_plugin.t_dynamic_action,',
'                                    p_plugin         IN apex_plugin.t_plugin)',
'  RETURN apex_plugin.t_dynamic_action_render_result IS',
'  --',
'  l_theme  VARCHAR2(500) := p_dynamic_action.attribute_01;',
'  l_result apex_plugin.t_dynamic_action_render_result;',
'  --',
'BEGIN',
'  -- Debug',
'  IF apex_application.g_debug THEN',
'    apex_plugin_util.debug_dynamic_action(p_plugin         => p_plugin,',
'                                          p_dynamic_action => p_dynamic_action);',
'  END IF;',
'  --',
'  -- Shepherd Theme CSS',
'  IF l_theme = ''shepherd-theme-dark'' THEN',
'    apex_css.add_file(p_name      => ''shepherd-theme-dark'',',
'                      p_directory => p_plugin.file_prefix);',
'  ELSIF l_theme = ''shepherd-theme-default'' THEN',
'    apex_css.add_file(p_name      => ''shepherd-theme-default'',',
'                      p_directory => p_plugin.file_prefix);',
'  ELSIF l_theme = ''shepherd-theme-square'' THEN',
'    apex_css.add_file(p_name      => ''shepherd-theme-square'',',
'                      p_directory => p_plugin.file_prefix);',
'  ELSIF l_theme = ''shepherd-theme-square-dark'' THEN',
'    apex_css.add_file(p_name      => ''shepherd-theme-square-dark'',',
'                      p_directory => p_plugin.file_prefix);',
'  END IF;',
'  -- Tether CSS',
'  apex_css.add_file(p_name      => ''tether.min'',',
'                    p_directory => p_plugin.file_prefix);',
'  --',
'  -- Shepherd JS + Tether JS',
'  apex_javascript.add_library(p_name           => ''tether.min'',',
'                              p_directory      => p_plugin.file_prefix,',
'                              p_version        => NULL,',
'                              p_skip_extension => FALSE);',
'  apex_javascript.add_library(p_name           => ''shepherd.min'',',
'                              p_directory      => p_plugin.file_prefix,',
'                              p_version        => NULL,',
'                              p_skip_extension => FALSE);',
'  -- Add JS Code to Start new Stepherd Instance',
'  apex_javascript.add_inline_code(p_code => ''var tour = new Shepherd.Tour({defaults: { classes: '''''' ||',
'                                            l_theme ||',
'                                            '''''',scrollTo: true}});'');',
'  --',
'  l_result.javascript_function := ''function(){null}'';',
'  l_result.attribute_01        := l_theme;',
'  RETURN l_result;',
'  --',
'END render_shepherdnewinstance;'))
,p_render_function=>'render_shepherdnewinstance'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_help_text=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'Dynamic Action Plugin using OpenSource JS framework "Stepherd"',
'This framework includes 3 DA Plugins.',
'Use all 3 on the same page, action is for example "page load"',
'1) Shepherd (New Instance) - Creates a new Stepherd Instance/Tour - Here you can choose the look&feel template class / theme (use once per page)',
'2) Shepherd (Add Step) - This Plugin adds one Step of the tour. Here you can choose title, text und position of the popup. Attached to an element css class. (can be used multiple times per page)',
'3) Shepherd (Start Tour) - This plugin is the last, and starts the guided tour from step to step. (use once per page)',
'Original from: http://github.hubspot.com/shepherd/'))
,p_version_identifier=>'1.0'
,p_about_url=>'https://github.com/Dani3lSun/apex-plugin-guidedtour'
,p_files_version=>21
);
